import React, { useState } from "react";
import Brice from "../component/IMAGES/Brice.svg";
import "./style.css";
import axios from "axios";
import { useNavigate,Link } from "react-router-dom";

const Login = () => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:5000/auth/adminlogin", values,{
        withCredentials: true,
      })
      
      .then((result) => {
        if (result.data.loginStatus) {
          navigate("/dashboard");
        } else {
          setError(result.data.error || "Login failed."); // Set a default error message
        }
      })
      .catch((error) => {
        console.error("Login error:", error);
        setError("Network error or login failed. Please try again."); // Inform user about generic error
      });
  };

  return (
    <div className=" d-flex justify-content-center align-items-center vh-100 loginpage">
      <div className="m-5">
        <img src={Brice} alt="hero" />
      </div>
      <div className="p-3 rounded w-25 border loginForm">
        <div className="text-warning">{error && error}</div>
        <h2>Login Page</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email">
              <strong>Email</strong>
            </label>
            <input
              type="email"
              name="email"
              autoComplete="off"
              placeholder="Enter Email"
              className="form-control rounded-0"
              onChange={(e) => setValues({ ...values, email: e.target.value })}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="password">
              <strong>Password</strong>
            </label>
            <input
              type="password"
              name="Password"
              autoComplete="off"
              placeholder="Enter Password"
              className="form-control rounded-0"
              onChange={(e) =>
                setValues({ ...values, password: e.target.value })
              }
            />
          </div>
          <button className="btn btn-success w-100 rounded-0">Log in</button>
          <div className="mb-3">
            <input type="checkbox" name="tick" id="tick" />
            <label htmlFor="email">
              You agree with the terms and conditions
            </label>
          </div>
          <div className="mb-3">
            <span>? </span>
            <Link
              to="/students/login"
              className="formSignup link"
              style={{ textDecoration: "none" }}
            >
              {" "}
              Login HERE
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
