import React, { useEffect, useState } from "react";
import axios from "axios";
const Home = () => {
  const [adminTotal, setAdminTotal] = useState();
  const [students, setStudentsTotal] = useState();
  const [department, setDepartmentTotal] = useState();
  const [admins, setAdmins] = useState([]);
  useEffect(() => {
    adminCount();
    studentsCont();
    departmentCount();
    AdminRecords();
  }, []);

  const adminCount = () => {
    axios.get("http://localhost:5000/auth/admin_count").then((result) => {
      if (result.data.Status) {
        setAdminTotal(result.data.Result[0].admin);
      }
    });
  };

  const studentsCont = () => {
    axios.get("http://localhost:5000/auth/students_count").then((result) => {
      if (result.data.Status) {
        setStudentsTotal(result.data.Result[0].students);
      }
    });
  };

  const AdminRecords = () => {
    axios.get("http://localhost:5000/auth/admin_records").then((result) => {
      if (result.data.Status) {
        setAdmins(result.data.Result);
      }else{
        alert(result.data.Error)
      }
    });
  };

  const departmentCount = () => {
    axios.get("http://localhost:5000/auth/department_count").then((result) => {
      if (result.data.Status) {
        setDepartmentTotal(result.data.Result[0].departmentOfEmp);
      }else{
        alert(result.data.Error)
      }
    });
  };

  return (
    <div>
      <div className="p-3 d-flex justify-content-around mt-3">
        <div className="px-3 pt-2 pb-3 border shadow-sm w-25">
          <div className="text-center pb-1">
            <h4>Admin</h4>
          </div>
          <hr />
          <div className="d-flex justify-content-between">
            <h5>Total:</h5>
            <h5>{adminTotal}</h5>
          </div>
        </div>

        <div className="px-3 pt-2 pb-3 border shadow-sm w-25">
          <div className="text-center pb-1">
            <h4>Students</h4>
          </div>
          <hr />
          <div className="d-flex justify-content-between">
            <h5>Total:</h5>
            <h5>{students}</h5>
          </div>
        </div>

        <div className="px-3 pt-2 pb-3 border shadow-sm w-25">
          <div className="text-center pb-1">
            <h4>Department</h4>
          </div>
          <hr />
          <div className="d-flex justify-content-between">
            <h5>Total:</h5>
            <h5>{department}</h5>
          </div>
        </div>
      </div>

      {/* display numbers of admins */}
      <div className="mt-4 px-5 pt-3">
        <h3>List of Admins</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {admins.map((a) => (
              <tr>
                <td>{a.email}</td>
                <td>
                    <button
                     
                      className="btn btn-info btn-sm me-2"
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-warning btn-sm"
                    >
                      Delete
                    </button>
                  </td>
              </tr>
            ))}
          </tbody>
        
        </table>
      </div>
    </div>
  );
};

export default Home;
